import * as actionTypes from './actionTypes'
import axios from 'axios'

const authStart = () => {
    return {
        type: actionTypes.AUTH_START,
    }
}

const authSuccess = (data) => {
    return {
        type: actionTypes.AUTH_SUCCESS,
        token: data.idToken,
        userId: data.localId,
    }
}

const authFail = (error) => {
    return {
        type: actionTypes.AUTH_FAIL,
        error: error,
    }
}

const authLogout = (expiresTime) => {
    return (dispatch) => {
        setTimeout(() => dispatch({ type: actionTypes.AUTH_LOGOUT }), expiresTime)
    }
}

export const auth = (username, password, isSignIn) => {
    const authData = {
        email: username,
        password: password,
        returnSecureToken: true,
    }
    const signUpUrl =
        'https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyDKbV6ttrbEDvWY4e2YCUo-ST6GxKX8lAY'
    const signInUrl =
        'https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyDKbV6ttrbEDvWY4e2YCUo-ST6GxKX8lAY'

    const url = isSignIn ? signInUrl : signUpUrl

    return (dispatch) => {
        dispatch(authStart())
        axios
            .post(url, authData)
            .then((response) => {
                dispatch(authSuccess(response.data))
                dispatch(authLogout(response.data.expiresIn))
            })
            .catch((error) => {
                dispatch(authFail(error.response.data.error.message))
            })
    }
}
