import * as actionTypes from '../actions/actionTypes'

const initialState = {
    ingredients: null,
    price: 4,
    error: false,
}

const INGREDIENTS_PRICE = {
    salad: 1,
    cheese: 1,
    bacon: 1.5,
    meat: 2,
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.ADD_INGREDIENT:
            return {
                ...state,
                ingredients: {
                    ...state.ingredients,
                    [action.ingredientName]: state.ingredients[action.ingredientName] + 1,
                },
                price: state.price + INGREDIENTS_PRICE[action.ingredientName],
            }
        case actionTypes.REMOVE_INGREDIENT:
            const qntIngredient = state.ingredients[action.ingredientName]
            const priceUpdated = state.price - INGREDIENTS_PRICE[action.ingredientName]
            return {
                ...state,
                ingredients: {
                    ...state.ingredients,
                    [action.ingredientName]: qntIngredient > 0 ? qntIngredient - 1 : 0,
                },
                price: qntIngredient > 0 ? priceUpdated : state.price,
            }
        case actionTypes.ORDER_MADE:
            return {
                ...initialState,
            }
        case actionTypes.INIT_INGREDIENTS:
            return {
                ...state,
                ingredients: {
                    salad: action.ingredientName.salad,
                    cheese: action.ingredientName.cheese,
                    bacon: action.ingredientName.bacon,
                    meat: action.ingredientName.meat,
                },
            }
        case actionTypes.INIT_INGREDIENTS_FAIL:
            return {
                ...state,
                error: true,
                ingredients: null,
            }
        default:
            return {
                ...state,
                ingredients: {
                    ...state.ingredients,
                },
            }
    }
}

export default reducer
