import React, { Component } from 'react'
import { connect } from 'react-redux'
import Input from '../../components/UI/Input/Input'
import Button from '../../components/UI/Button/Button'
import * as actions from '../../store/actions/index'
import './Auth.css'

class Auth extends Component {
    state = {
        controls: {
            name: {
                elementType: 'input',
                config: {
                    type: 'text',
                    placeholder: 'Username',
                },
                value: '',
                validation: {
                    required: true,
                },
                valid: false,
                touched: false,
            },
            password: {
                elementType: 'input',
                config: {
                    type: 'password',
                    placeholder: 'Password',
                },
                value: '',
                validation: {
                    required: true,
                    minLength: 6,
                    maxLength: 15,
                },
                valid: false,
                touched: false,
            },
        },
        validForm: false,
    }

    inputChangeHandler = (event, inputElement) => {
        const rules = this.state.controls[inputElement].validation
        const updatedControls = {
            ...this.state.controls,
            [inputElement]: {
                ...this.state.controls[inputElement],
                value: event.target.value,
                valid: this.checkValid(event.target.value, rules),
                touched: true,
            },
        }

        const validPassword = this.state.controls.password.valid
        const validUsername = this.state.controls.name.valid
        this.setState({ controls: updatedControls, validForm: validPassword && validUsername })
    }

    checkValid = (value, rules) => {
        let valid = true
        let changed = false

        if (rules) {
            if (rules.required) {
                valid = value.trim() !== '' && valid
                changed = true
            }
            if (rules.minLength) {
                valid = value.length >= rules.minLength && valid
                changed = true
            }
            if (rules.maxLength) {
                valid = value.length <= rules.maxLength && valid
                changed = true
            }

            if (rules.isEmail) {
                const emailRegex = `^\S+@\S+$`
            }
        }

        return changed ? valid : false
    }

    submitHandler = (event) => {
        event.preventDefault()
        const username = this.state.controls.name.value
        const password = this.state.controls.password.value
        this.props.onAuthSubmit(username, password)
    }

    render() {
        const inputs = []

        for (const key in this.state.controls) {
            const input = (
                <Input
                    key={key}
                    elementType={this.state.controls[key].elementType}
                    config={this.state.controls[key].config}
                    value={this.state.controls[key].value}
                    invalid={!this.state.controls[key].valid}
                    shouldValidate={this.state.controls[key].validation}
                    touched={this.state.controls[key].touched}
                    changed={(event) => this.inputChangeHandler(event, key)}
                />
            )
            inputs.push(input)
        }

        return (
            <div className='Auth'>
                <form onSubmit={this.submitHandler}>
                    {inputs.length > 0 ? inputs : null}
                    <Button
                        btnType='Success'
                        disabled={!this.state.validForm}
                        // clicked={this.submitHandler}
                    >
                        Submit
                    </Button>
                </form>
            </div>
        )
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        onAuthSubmit: (username, password) => dispatch(actions.auth(username, password)),
    }
}

export default connect(null, mapDispatchToProps)(Auth)
