import React, { Component } from 'react'
import Aux from '../../hoc/Aux'
import Burger from '../../components/Burger/Burger'
import BuildControls from '../../components/Burger/BuildControls/BuildControls'
import Modal from '../../components/UI/Modal/Modal'
import OrderSummary from '../../components/Burger/OrderSummary/OrderSummary'
import axios from '../../axios-orders'

const INGREDIENTS_PRICE = {
    salad: 1,
    cheese: 1,
    bacon: 1.5,
    meat: 2
}

class BurgerBuilder extends Component {
    state = {
        ingredients: {
            salad: 0,
            bacon: 0,
            cheese: 0,
            meat: 0
        },
        price: 4,
        purchasable: false,
        purchasing: false,
        loading: false
    }

    updatePurchasable = ingredients => {
        const sum = Object.values(ingredients)
            .reduce((sum, el) => sum += el, 0)

        this.setState({purchasable: sum > 0})    
    }

    addIngredientHandler = type => {
        const updatedIngredients = {...this.state.ingredients}
        updatedIngredients[type] += 1

        const updatedPrice = this.state.price + INGREDIENTS_PRICE[type]

        this.setState({price: updatedPrice, ingredients: updatedIngredients})
        this.updatePurchasable(updatedIngredients)
    }

    removeIngredientHandler = type => {
        const updatedIngredients = {...this.state.ingredients}

        if (updatedIngredients[type] > 0) {
            updatedIngredients[type] -= 1

            const updatedPrice = this.state.price - INGREDIENTS_PRICE[type]
            this.setState({price: updatedPrice, ingredients: updatedIngredients})
        }
        this.updatePurchasable(updatedIngredients)
    }

    purchaseHandler = () => {
        this.setState({purchasing: true})
    }

    purchaseCancelHandler = () => {
        this.setState({purchasing: false})
    }

    purchaseContinueHandler = () => {
        const order = {
            ingredient: this.state.ingredients,
            price: this.state.price,
            customer: {
                name: 'Thiaguin',
                delivery: 'fastest',
                address: {
                    number: 371,
                    cep: '58417-598',
                    country: 'Brazil'  
                }
            }
        }
        this.setState({loading: true})
        axios.post('/orders.json', order)
            .then((response) => {
                this.setState({loading: false, purchasing: false})
            })
            .catch((error) => console.log(error))
    }
    render() {
        const disableInfo = {}

        for (const ingredient of Object.keys(this.state.ingredients)) {
            disableInfo[ingredient] = this.state.ingredients[ingredient] <= 0
        }

        return (
            <Aux>
                <Modal show={this.state.purchasing} closing={this.purchaseCancelHandler}> 
                    <OrderSummary 
                        price={this.state.price}
                        cancelingPurchase={this.purchaseCancelHandler}
                        continuingPurchase={this.purchaseContinueHandler}
                        ingredients={this.state.ingredients}/>
                </Modal>
                <Burger ingredients={this.state.ingredients}/>
                <BuildControls 
                    addIngredients={this.addIngredientHandler}
                    removeIngredients={this.removeIngredientHandler}
                    disabled={disableInfo}
                    price={this.state.price}
                    ordered={this.purchaseHandler}
                    purchasable={this.state.purchasable}
                />
            </Aux>
        )
    }
}

export default BurgerBuilder
