import React, { Component } from 'react'
import Button from '../../../components/UI/Button/Button'
import Input from '../../../components/UI/Input/Input'
import axios from '../../../axios-orders'
import './ContactData.css'

class ContactData extends Component {
    state = {
        orderForm: {
            name: {
                elementType: 'input',
                config: {
                    type: 'text',
                    placeholder: 'Your Name',
                },
                value: '',
                validation: {
                    required: true,
                },
                valid: false,
            },
            email: {
                elementType: 'input',
                config: {
                    type: 'text',
                    placeholder: 'Your Email',
                },
                value: '',
                validation: {
                    required: true,
                },
                valid: false,
            },
            street: {
                elementType: 'input',
                config: {
                    type: 'text',
                    placeholder: 'Street',
                },
                value: '',
                validation: {
                    required: true,
                },
                valid: false,
            },
            postalCode: {
                elementType: 'input',
                config: {
                    type: 'text',
                    placeholder: 'Postal Code',
                },
                value: '',
                validation: {
                    required: true,
                    maxLength: 8,
                    // minLength: 8,
                },
                valid: false,
            },
            country: {
                elementType: 'input',
                config: {
                    type: 'text',
                    placeholder: 'Country',
                },
                value: '',
                validation: {
                    required: true,
                },
                valid: false,
            },
            delivery: {
                elementType: 'select',
                config: {
                    options: [
                        { value: 'fastest', displayName: 'Fastest' },
                        { value: 'cheapset', displayName: 'Cheapest' },
                    ],
                },
                value: '',
                validation: {
                    required: true,
                },
                valid: false,
            },
        },
        loading: false,
    }

    orderHandler = (event) => {
        event.preventDefault()
        const formData = {}
        for (const key in this.state.orderForm) {
            formData[key] = this.state.orderForm[key].value
        }
        const order = {
            ingredients: this.props.ingredients,
            price: this.props.price,
            orderData: formData,
        }

        this.setState({ loading: true })
        axios
            .post('/orders.json', order)
            .then((response) => {
                this.setState({ loading: false })
                this.props.history.push('/')
            })
            .catch((error) => {
                this.setState({ loading: false })
            })
    }

    inputChangeHandler = (event, inputElement) => {
        const updatedOrderForm = { ...this.state.orderForm }
        const rules = updatedOrderForm[inputElement].validation
        const { value } = event.target
        updatedOrderForm[inputElement].value = value
        updatedOrderForm[inputElement].valid = this.checkValid(value, rules)
        console.log(updatedOrderForm)
        this.setState({ orderForm: updatedOrderForm })
    }

    checkValid = (value, rules) => {
        let valid = true

        if (rules.required) valid = value.trim() === '' && valid
        if (rules.minLength) valid = value.length >= rules.minLength && valid
        if (rules.maxLength) {
            console.log(rules.maxLength, value.length, valid)
            valid = value.length >= rules.maxLength && valid
        }

        return valid
    }

    render() {
        const inputs = []

        for (const key in this.state.orderForm) {
            const input = (
                <Input
                    key={key}
                    elementType={this.state.orderForm[key].elementType}
                    config={this.state.orderForm[key].config}
                    value={this.state.orderForm[key].value}
                    changed={(event) => this.inputChangeHandler(event, key)}
                />
            )
            inputs.push(input)
        }

        return (
            <div className='ContactData'>
                <h4>Please enter with your datas!</h4>
                <form>
                    {inputs.length > 0 ? inputs : null}
                    <Button btnType='Success' clicked={this.orderHandler}>
                        Order
                    </Button>
                </form>
            </div>
        )
    }
}

export default ContactData
