import React from 'react'
import './Input.css'

const input = (props) => {
    let input

    switch (props.elementType) {
        case 'textarea':
            input = (
                <textarea
                    className='InputElement'
                    {...props.config}
                    value={props.value}
                />
            )
            break
        case 'select':

        default:
            input = (
                <input
                    className='InputElement'
                    {...props.config}
                    value={props.value}
                />
            )
    }

    return (
        <div className='Input'>
            <label className='Label'>{props.label}</label>
            {input}
        </div>
    )
}

export default input
