import * as actionTypes from './actionTypes'
import axios from '../../axios-orders'

export const addIngredient = (name) => {
    return {
        type: actionTypes.ADD_INGREDIENT,
        ingredientName: name,
    }
}

export const removeIngredient = (name) => {
    return {
        type: actionTypes.REMOVE_INGREDIENT,
        ingredientName: name,
    }
}

const initIngredientsSync = (ingredients) => {
    return {
        type: actionTypes.INIT_INGREDIENTS,
        ingredientsFetched: ingredients,
    }
}

const initIngredientsFail = () => {
    return {
        type: actionTypes.INIT_INGREDIENTS_FAIL,
    }
}

export const initIngredients = () => {
    return (dispatch) => {
        axios
            .get(' https://burger-builfdder-35ab4.firesbaseio.com/.json')
            .then((response) => {
                dispatch(initIngredientsSync(response.data))
            })
            .catch((error) => {
                dispatch(initIngredientsFail())
            })
    }
}
