import React from 'react'
import './Input.css'

const input = (props) => {
    let input

    switch (props.inputtype) {
        case ('textarea'):
            input = <textarea className='InputElement' {...props}/>
            break
        default:
            input = <input className='InputElement' {...props}/>    
    }

    return (
        <div className='Input'>
            <label className='Label'>{props.label}</label>
            {input}
        </div>
    )
}

export default input
