import React from 'react'
import './Input.css'

const input = (props) => {
    let input

    switch (props.elementType) {
        case 'textarea':
            input = (
                <textarea
                    className='InputElement'
                    {...props.config}
                    value={props.value}
                />
            )
            break
        case 'select':
            const options = props.config.options.map((option) => (
                <option value={option.value}>{option.displayName}</option>
            ))
            input = (
                <select
                    className='InputElement'
                    onChange={props.changed}
                    value={props.value}
                >
                    {options}
                </select>
            )
            break
        default:
            input = (
                <input
                    className='InputElement'
                    {...props.config}
                    value={props.value}
                />
            )
    }

    return (
        <div className='Input'>
            <label className='Label'>{props.label}</label>
            {input}
        </div>
    )
}

export default input
