import React, { Component } from 'react'
import Input from '../../components/UI/Input/Input'
import Button from '../../components/UI/Button/Button'

class Auth extends Component {
    state = {
        controls: {
            name: {
                elementType: 'input',
                config: {
                    type: 'text',
                    placeholder: 'Username',
                },
                value: '',
                validation: {
                    required: true,
                },
                valid: false,
                touched: false,
            },
            password: {
                elementType: 'input',
                config: {
                    type: 'password',
                    placeholder: 'Password',
                },
                value: '',
                validation: {
                    required: true,
                },
                valid: false,
                touched: false,
            },
        },
    }

    inputChangeHandler = (event, inputElement) => {
        const updatedOrderForm = { ...this.state.controls }
        const rules = updatedOrderForm[inputElement].validation
        const { value } = event.target
        let validForm = true

        updatedOrderForm[inputElement].value = value
        updatedOrderForm[inputElement].valid = this.checkValid(value, rules)
        updatedOrderForm[inputElement].touched = true

        for (const key in updatedOrderForm) {
            const value = updatedOrderForm[key]
            validForm = validForm && value.valid
        }

        this.setState({ controls: updatedOrderForm, validFormData: validForm })
    }

    render() {
        const inputs = []

        for (const key in this.state.controls) {
            const input = (
                <Input
                    key={key}
                    elementType={this.state.controls[key].elementType}
                    config={this.state.controls[key].config}
                    value={this.state.controls[key].value}
                    invalid={!this.state.controls[key].valid}
                    shouldValidate={this.state.controls[key].validation}
                    touched={this.state.controls[key].touched}
                    changed={(event) => this.inputChangeHandler(event, key)}
                />
            )
            inputs.push(input)
        }

        return (
            <div>
                <form>
                    {inputs.length > 0 ? inputs : null}
                    <Button
                        btnType='Success'
                        disabled={!this.state.validFormData}
                        clicked={this.orderHandler}
                    >
                        Submit
                    </Button>
                </form>
            </div>
        )
    }
}

export default Auth
