import React, { useState, useEffect } from 'react'
import Aux from './Aux'
import Modal from '../components/UI/Modal/Modal'

const withErrorHandler = (WrappedComponent, axios) => {
    return (props) => {
        // state = {
        //     error: null,
        // }
        const [error, setError] = useState(null)
        let reqInterceptor
        let resInterceptor
        const { request, response } = axios.interceptors
        useEffect(() => {
            reqInterceptor = request.use((req) => {
                setError(null)
                return req
            })
        }, [reqInterceptor, request])

        useEffect(() => {
            resInterceptor = response.use(
                (res) => res,
                (error) => {
                    this.setState({ error: error })
                }
            )
        }, [resInterceptor, response])
        // componentDidMount() {
        //     this.reqInterceptor = axios.interceptors.request.use((req) => {
        //         this.setState({ error: null })
        //         return req
        //     })

        //     this.resInterceptor = axios.interceptors.response.use(
        //         (res) => res,
        //         (error) => {
        //             this.setState({ error: error })
        //         }
        //     )
        // }

        // componentWillUnmount() {
        //     axios.interceptors.request.eject(this.reqInterceptor)
        //     axios.interceptors.response.eject(this.resInterceptor)
        // }

        const closeErrorHandler = () => {
            this.setState({ error: null })
        }

        return (
            <Aux>
                <Modal show={this.state.error} closing={this.closeErrorHandler}>
                    {this.state.error ? this.state.error.message : null}
                </Modal>
                <WrappedComponent {...this.props} />
            </Aux>
        )
    }
}

export default withErrorHandler
