import React, { useState, useEffect } from 'react'
import Aux from './Aux'
import Modal from '../components/UI/Modal/Modal'

const withErrorHandler = (WrappedComponent, axios) => {
    return (props) => {
        const { request, response } = axios.interceptors
        const [error, setError] = useState(null)

        const reqInterceptor = axios.interceptors.request.use((req) => {
            setError(null)
            return req
        })

        const resInterceptor = axios.interceptors.response.use(
            (res) => res,
            (err) => {
                setError(err.response.data.error)
            }
        )

        useEffect(() => {
            return () => {
                request.eject(reqInterceptor)
            }
        }, [request, reqInterceptor])

        useEffect(() => {
            return () => {
                response.eject(resInterceptor)
            }
        }, [response, resInterceptor])

        const closeErrorHandler = () => {
            setError(null)
        }
        if (error) {
            console.log('sa', Object.keys(error))
            console.log('dafs', error.response.data.error)
        }
        return (
            <Aux>
                <Modal show={error} closing={closeErrorHandler}>
                    {error ? error.message : null}
                </Modal>
                <WrappedComponent {...props} />
            </Aux>
        )
    }
}

export default withErrorHandler
