import React, { Component } from 'react'
import { connect } from 'react-redux'
import Aux from '../../hoc/Aux'
import Burger from '../../components/Burger/Burger'
import BuildControls from '../../components/Burger/BuildControls/BuildControls'
import Modal from '../../components/UI/Modal/Modal'
import Spinner from '../../components/UI/Spinner/Spinner'
import OrderSummary from '../../components/Burger/OrderSummary/OrderSummary'
import axios from '../../axios-orders'
import withErrorHandler from '../../hoc/withErrorHandler'

const INGREDIENTS_PRICE = {
    salad: 1,
    cheese: 1,
    bacon: 1.5,
    meat: 2,
}

class BurgerBuilder extends Component {
    state = {
        // ingredients: null,
        price: 4,
        purchasable: false,
        purchasing: false,
        loading: false,
        error: false,
    }

    componentDidMount() {
        // axios.get('ingredients.json')
        //     .then((response) => {
        //         this.setState({ingredients: response.data})
        //     })
        //     .catch((error) => {
        //         this.setState({error: true})
        //     })
    }

    updatePurchasable = (ingredients) => {
        const sum = Object.values(ingredients).reduce((sum, el) => (sum += el), 0)

        this.setState({ purchasable: sum > 0 })
    }

    addIngredientHandler = (type) => {
        const updatedIngredients = { ...this.state.ingredients }
        updatedIngredients[type] += 1

        const updatedPrice = this.state.price + INGREDIENTS_PRICE[type]

        this.setState({ price: updatedPrice, ingredients: updatedIngredients })
        this.updatePurchasable(updatedIngredients)
    }

    removeIngredientHandler = (type) => {
        const updatedIngredients = { ...this.state.ingredients }

        if (updatedIngredients[type] > 0) {
            updatedIngredients[type] -= 1

            const updatedPrice = this.state.price - INGREDIENTS_PRICE[type]
            this.setState({ price: updatedPrice, ingredients: updatedIngredients })
        }
        this.updatePurchasable(updatedIngredients)
    }

    purchaseHandler = () => {
        this.setState({ purchasing: true })
    }

    purchaseCancelHandler = () => {
        this.setState({ purchasing: false })
    }

    purchaseContinueHandler = () => {
        const queryParams = []
        for (const ingredient in this.props.ingredients) {
            const param =
                encodeURIComponent(ingredient) +
                '=' +
                encodeURIComponent(this.props.ingredients[ingredient])
            queryParams.push(param)
        }
        queryParams.push('price=' + this.state.price)
        this.props.history.push({
            pathname: '/checkout',
            search: `?${queryParams.join('&')}`,
        })
    }

    render() {
        const disableInfo = {}
        let orderSummary = null
        let burger = this.state.error ? (
            <p style={{ textAlign: 'center' }}>Ingredients can't be loaded!</p>
        ) : (
            <Spinner />
        )

        if (this.state.loading) {
            orderSummary = <Spinner />
        }

        if (this.props.ingredients) {
            for (const ingredient of Object.keys(this.props.ingredients)) {
                disableInfo[ingredient] = this.props.ingredients[ingredient] <= 0
            }
            burger = (
                <Aux>
                    <Burger ingredients={this.props.ingredients} />
                    <BuildControls
                        addIngredients={this.addIngredientHandler}
                        removeIngredients={this.removeIngredientHandler}
                        disabled={disableInfo}
                        price={this.state.price}
                        ordered={this.purchaseHandler}
                        purchasable={this.state.purchasable}
                    />
                </Aux>
            )

            orderSummary = (
                <OrderSummary
                    price={this.state.price}
                    cancelingPurchase={this.purchaseCancelHandler}
                    continuingPurchase={this.purchaseContinueHandler}
                    ingredients={this.props.ingredients}
                />
            )
        }

        return (
            <Aux>
                <Modal show={this.state.purchasing} closing={this.purchaseCancelHandler}>
                    {orderSummary}
                </Modal>
                {burger}
            </Aux>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        ingredients: state.ingredients,
    }
}
export default connect(mapStateToProps)(withErrorHandler(BurgerBuilder, axios))
