import React, {Component} from 'react'
import Button from '../../../components/UI/Button/Button'
import axios from '../../../axios-orders'
import './ContactData.css'

class ContactData extends Component {
    state = {
        name: '',
        email: '',
        address: {
            street: '',
            postalCode: ''
        },
        loading: false
    }

    orderHandler = () => {
        console.log(this.props)
        const order = {
            ingredient: this.props.ingredients,
            price: this.props.price,
            // customer: {
            //     name: 'Thiaguin',
            //     delivery: 'fastest',
            //     address: {
            //         number: 371,
            //         cep: '58417-598',
            //         country: 'Brazil'  
            //     }
            // }
        }
        this.setState({loading: true})
        axios.post('/orders.json', order)
            .then((response) => {
                console.log(response)
                this.setState({loading: false})
            })
            .catch((error) => {
                this.setState({loading: false})
            })

    }

    render () {
        return (
            <div className='ContactData'>
                <h4>Please enter with your datas!</h4>
                <form>
                    <input type='text' className='Input' name='name' placeholder='Your Name'></input>
                    <input type='email' className='Input' name='email' placeholder='Your Email'></input>
                    <input type='text' className='Input' name='street' placeholder='Street'></input>
                    <input type='text' className='Input' name='postalCode' placeholder='Postal Code'></input>
                    <Button btnType='Success' clicked={this.orderHandler}>Order</Button>
                </form>
            </div>
        )
    }
} 

export default ContactData
