import React from 'react'
import './Order.css'

const order = props => {
    const styleIngredients = {
        textTransform: 'capitalize',
        display: 'inline-block',
        margin: '0 8px',
        border: '1px solid #ccc',
        padding: '5'
    }

    const ingredients = []

    for (const ingredient in props.ingredients) {
         ingredients.push({
             name: ingredient,
             quantity: props.ingredients[ingredient]
         })   
    }

    const ingredientsOutput = ingredients.map(ig => {
        return (
            <span style={styleIngredients} key={ig.name}>{ig.name} ({ig.quantity})</span>
        )
    })

    return (
        <div className='Order'>
            <p>Ingredients: {ingredientsOutput}</p>
            <p>Price <strong>R$ {Number.parseFloat(props.price).toFixed(2)}</strong></p>
        </div>
    )
} 


export default order
