import React from 'react'
import './Order.css'

const order = props => {
    const ingredients = []

    for (const ingredient in props.ingredients) {
         ingredients.push({
             name: ingredient,
             quantity: props.ingredients[ingredient]
         })   
    }

    return (
        <div className='Order'>
            <p>Ingredients: {ingredients}</p>
            <p>Price <strong>R$ {Number.parseFloat(props.price).toFixed(2)}</strong></p>
        </div>
    )
} 


export default order
