import * as actionTypes from './actionTypes'
import axios from 'axios'

const authStart = () => {
    return {
        type: actionTypes.AUTH_START,
    }
}

const authSuccess = (data) => {
    const expirationDate = new Date(new Date() + data.expiresIn + 1000)
    const date = new Date()
    date.setSeconds(date.getSeconds() + data.expiresIn)
    console.log('date', date)
    console.log('expiration date', expirationDate)
    localStorage.setItem('token', data.idToken)
    localStorage.setItem('expirationDate', expirationDate)

    return {
        type: actionTypes.AUTH_SUCCESS,
        token: data.idToken,
        userId: data.localId,
    }
}

const authFail = (error) => {
    return {
        type: actionTypes.AUTH_FAIL,
        error: error,
    }
}

export const authLogout = () => {
    localStorage.removeItem('token')
    localStorage.removeItem('expirationDate')
    return {
        type: actionTypes.AUTH_LOGOUT,
    }
}

const authLogoutAsync = (expiresTime) => {
    return (dispatch) => {
        setTimeout(() => dispatch(authLogout()), expiresTime * 1000)
    }
}

export const auth = (username, password, isSignIn) => {
    const authData = {
        email: username,
        password: password,
        returnSecureToken: true,
    }
    const signUpUrl =
        'https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyDKbV6ttrbEDvWY4e2YCUo-ST6GxKX8lAY'
    const signInUrl =
        'https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyDKbV6ttrbEDvWY4e2YCUo-ST6GxKX8lAY'

    const url = isSignIn ? signInUrl : signUpUrl

    return (dispatch) => {
        dispatch(authStart())
        axios
            .post(url, authData)
            .then((response) => {
                dispatch(authSuccess(response.data))
                dispatch(authLogoutAsync(response.data.expiresIn))
            })
            .catch((error) => {
                dispatch(authFail(error.response.data.error.message))
            })
    }
}

export const authRedirectPath = (path) => {
    return {
        type: actionTypes.AUTH_REDIRECT_PATH,
        path: path,
    }
}
