import React, { useEffect } from 'react'
import { Route, Switch, withRouter, Redirect } from 'react-router-dom'
import { connect } from 'react-redux'

import Layout from './containers/Layout/Layout'
import BurgerBuilder from './containers/BurgerBuilder/BurgerBuilder'
import Checkout from './containers/Checkout/Checkout'
import Orders from './containers/Orders/Orders'
import Auth from './containers/Auth/Auth'
import Logout from './containers/Auth/Logout/Logout'

import * as actions from './store/actions/index'

const checkoutComponent = React.lazy(() => {
    return import('./containers/Checkout/Checkout')
})

const ordersComponent = React.lazy(() => {
    return import('./containers/Orders/Orders')
})

const logoutComponent = React.lazy(() => {
    return import('./containers/Auth/Logout/Logout')
})

const App = (props) => {
    const { onAuthCheck } = props

    useEffect(() => {
        onAuthCheck()
    }, [onAuthCheck])

    let routes = (
        <Switch>
            <Route path='/auth' component={Auth} />
            <Route path='/' component={BurgerBuilder} />
            <Redirect to='/' />
        </Switch>
    )

    if (props.isAuth) {
        routes = (
            <Switch>
                <Route path='/orders' component={ordersComponent} />
                <Route path='/checkout' component={checkoutComponent} />
                <Route path='/auth' component={Auth} />
                <Route path='/logout' component={logoutComponent} />
                <Route path='/' component={BurgerBuilder} />
            </Switch>
        )
    }
    return (
        <div>
            <Layout>{routes}</Layout>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        isAuth: !!state.auth.token,
    }
}

const mapDispathcToProps = (dispatch) => {
    return {
        onAuthCheck: () => dispatch(actions.authCheck()),
    }
}
export default withRouter(connect(mapStateToProps, mapDispathcToProps)(App))
