import React from 'react'
import './Modal.css'

const classes = 'Modal'

const modal = props => (
    <div 
        className={classes}
        style={{
            transform: props.show ? 'translateY(0)' : 'translateY(-100vh)',
            opacity: props.show ? '1' : '0'
        }}>
        {props.children}
    </div>
)

export default modal
